function animateNumber(element, values, duration) {
    let index = 0; const interval = duration / values.length; let startTime = null; function updateNumber(timestamp) {
        if (!startTime) startTime = timestamp; const elapsed = timestamp - startTime; if (elapsed >= interval * index && index < values.length) { element.textContent = values[index]; index++ }
        if (index < values.length) { requestAnimationFrame(updateNumber) } else { setTimeout(() => { index = 0; startTime = null; requestAnimationFrame(updateNumber) }, 1000) }
    }
    requestAnimationFrame(updateNumber)
}
const numberElement = document.querySelector('.footer_list_icon_number'); 
